<script>
export default {
  name: "ErrorPage",
  data() {
    return {
      sec: 6,
      timer: null,
      errStatus: 404,
    };
  },
  mounted: function(){
    this.init();
  },
  methods: {
    backToIndex() {
      this.$router.push({ path: '/' });
    },
    init() {
      this.errStatus = parseInt(this.$route.query.status);
      if(this.errStatus == 404) {
        document.title = '404 未找到 - 梦欤的管理系统项目';
        this.timer = setInterval(() => {
          if(this.sec > 0) this.sec --;
          else {
            clearInterval(this.timer);
            this.$router.push({ path: '/' });
          }
        }, 1000)
      }else if(this.errStatus == 403) {
        document.title = '403 无权限 - 梦欤的管理系统项目';
      }else if(this.errStatus == 404) {
        document.title = '500 服务器故障 - 梦欤的管理系统项目';
      }
    }
  }
};
</script>



